import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, Image, StyleSheet, TextInput, TouchableOpacity } from 'react-native';
import axios from 'axios';
import { useFonts } from 'expo-font';
import { Ionicons } from '@expo/vector-icons';

const GamesList = () => {
  const [gamesData, setGamesData] = useState([]);
  const [filteredGamesData, setFilteredGamesData] = useState([]);
  const [searchText, setSearchText] = useState('');
  const [selectedGame, setSelectedGame] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://www.freetogame.com/api/games');
        setGamesData(response.data);
      } catch (error) {
        console.log('Hubo un error:', error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const filteredGames = gamesData.filter((game) =>
      game.title.toLowerCase().includes(searchText.toLowerCase())
    );
    setFilteredGamesData(filteredGames);
  }, [gamesData, searchText]);

  const [loaded] = useFonts({
    'Anton': require('../../assets/fonts/Anton-Regular.ttf'),
    'Rubik': require('../../assets/fonts/Rubik-Regular.ttf'),
  });

  if (!loaded) {
    return null;
  }

  const handleGamePress = (description) => {
    setSelectedGame(description);
  };

  return (
    <View style={styles.viewList}>
      <View style={styles.searchContainer}>
        <Ionicons name="search" size={25} style={styles.searchIcon} />
        <TextInput
          placeholder="Buscar..."
          value={searchText}
          onChangeText={setSearchText}
          style={styles.textInput}
        />
      </View>

      {filteredGamesData.length === 0 ? (
        <Text>No se encontraron juegos</Text>
      ) : (
        <FlatList
          data={filteredGamesData}
          keyExtractor={(item) => item.id.toString()}
          renderItem={({ item }) => (
            <TouchableOpacity onPress={() => handleGamePress(item.description)}>
              <View style={styles.gameContainer}>
                <Text style={styles.texto}>Nombre: {item.title}</Text>
                <Text style={styles.texto2}>Categor√≠a: {item.genre}</Text>
                <Text style={styles.texto2}>Publicador: {item.publisher}</Text>
                <Text style={styles.texto2}>Fecha de lanzamiento: {item.release_date}</Text>
                <Text style={styles.texto2}>Plataforma: {item.platform}</Text>
                <Image style={styles.img} source={{ uri: item.thumbnail }} />
              </View>
            </TouchableOpacity>
          )}
        />
      )}

      {selectedGame && (
        <View style={styles.descriptionContainer}>
          <Text style={styles.descriptionText}>{selectedGame}</Text>
        </View>
      )}
    </View>
  );
};

const styles = StyleSheet.create({
  searchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#ffff',
    borderRadius: 50,
    paddingHorizontal: 10,
    paddingVertical: 5,
    marginHorizontal: 90,
    opacity: 0.5
  },
  searchIcon: {
    marginRight: 10,
  },
  textInput: {
    flex: 1,
  },
  viewList: {
    position: 'relative',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: "#111625"
  },
  gameContainer: {
    alignItems: 'center',
    justifyContent: 'center',
    marginVertical: 10,
  },
  texto: {
    fontWeight: 'bold',
    fontSize: 25,
    color: "#abccbd",
    fontFamily: 'Rubik'
  },
  texto2: {
    fontSize: 15,
    fontStyle: 'italic',
    color: "#fffbf0",
  },
  img: {
    width: 280,
    height: 150,
    borderRadius: 20,
  },
  descriptionContainer: {
    marginVertical: 10,
    padding: 10,
    backgroundColor: '#ffffff',
  },
  descriptionText: {
    fontSize: 16,
    color: '#000000',
  },
});

export default GamesList;
