import React, { useEffect, useState } from 'react';
import { View, Text, FlatList, Image, StyleSheet, TextInput, TouchableOpacity, Modal } from 'react-native';
import axios from 'axios';
import { useFonts } from 'expo-font';
import { Ionicons } from '@expo/vector-icons';

const GamesList = () => {
  const [gamesData, setGamesData] = useState([]);
  const [filteredGamesData, setFilteredGamesData] = useState([]);
  const [searchText, setSearchText] = useState('');
  const [modalVisible, setModalVisible] = useState(false);
  const [selectedGame, setSelectedGame] = useState(null);

  useEffect(() => {
    const fetchData = async () => {
      try {
        const response = await axios.get('https://www.freetogame.com/api/games');
        setGamesData(response.data);
      } catch (error) {
        console.log('Hubo un error:', error);
      }
    };

    fetchData();
  }, []);

  useEffect(() => {
    const filteredGames = gamesData.filter((game) =>
      game.title.toLowerCase().includes(searchText.toLowerCase())
    );
    setFilteredGamesData(filteredGames);
  }, [gamesData, searchText]);

  const [loaded] = useFonts({
    'Anton': require('../../assets/fonts/Anton-Regular.ttf'),
    'Rubik': require('../../assets/fonts/Rubik-Regular.ttf'),
  });

  if (!loaded) {
    return null;
  }

  const handleImagePress = (game) => {
    setSelectedGame(game);
    setModalVisible(true);
  };

  return (
    <View style={styles.viewList}>
      <View style={styles.searchContainer}>
        <Ionicons name="search" size={25} style={styles.searchIcon} />
        <TextInput
          placeholder="Buscar..."
          value={searchText}
          onChangeText={setSearchText}
          style={styles.textInput}
        />
      </View>

      {filteredGamesData.length === 0 ? (
        <View>
          <Ionicons name="search-off" size={25} color="white" />
          <Text style={styles.texto2}>No se encontraron juegos</Text>
        </View>

      ) : (
        <FlatList
          data={filteredGamesData}
          keyExtractor={(item) => item.id.toString()}
          renderItem={({ item }) => (
            <View style={styles.viewList}>
              <Text style={styles.texto}>Nombre: {item.title}</Text>
              <Text style={styles.texto2}>Categoría: {item.genre}</Text>
              <Text style={styles.texto2}>Publicador: {item.publisher}</Text>
              <Text style={styles.texto2}>Fecha de lanzamiento: {item.release_date}</Text>
              <Text style={styles.texto2}>Plataforma: {item.platform}</Text>
              <TouchableOpacity onPress={() => handleImagePress(item)}>
                <Image style={styles.img} source={{ uri: item.thumbnail }} />
              </TouchableOpacity>
            </View>
          )}
        />
      )}

      <Modal
        visible={modalVisible}
        transparent={true}
        animationType="fade"
        onRequestClose={() => setModalVisible(false)}
      >
        <View style={styles.modalContainer}>
          <View style={styles.modalContent}>
            <Text style={styles.modalTitle}>{selectedGame?.title}</Text>
            <Text style={styles.modalDescription}>{selectedGame?.short_description}</Text>
            <TouchableOpacity onPress={() => setModalVisible(false)}>
              <Text style={styles.modalCloseButton}>Cerrar</Text>
            </TouchableOpacity>
          </View>
        </View>
      </Modal>
    </View>
  );
};

const styles = StyleSheet.create({
  searchContainer: {
    flexDirection: 'row',
    alignItems: 'center',
    backgroundColor: '#ffff',
    borderRadius: 50,
    paddingHorizontal: 10,
    paddingVertical: 5,
    marginHorizontal: 90,
    opacity: 0.5
  },
  searchIcon: {
    marginRight: 10,
  },
  textInput: {
    flex: 1,
  },
  viewList: {
    position: 'relative',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: "#111625"
  },
  texto: {
    fontWeight: 'bold',
    fontSize: 25,
    color: "#abccbd",
    fontFamily: 'Rubik'
  },
  texto2: {
    fontSize: 15,
    fontStyle: 'italic',
    color: "#fffbf0",
  },
  img: {
    width: 280,
    height: 150,
    borderRadius: 20,
  },
  modalContainer: {
    flex: 1,
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: 'rgba(0, 0, 0, 0.5)',
  },
  modalContent: {
    backgroundColor: '#fff',
    padding: 20,
    borderRadius: 10,
    alignItems: 'center',
  },
  modalTitle: {
    fontWeight: 'bold',
    fontSize: 20,
    marginBottom: 10,
  },
  modalDescription: {
    fontSize: 16,
    marginBottom: 20,
  },
  modalCloseButton: {
    color: '#0000ff',
    fontSize: 18,
    fontWeight: 'bold',
  },
});

export default GamesList;
