import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, TouchableOpacity, View } from 'react-native';
import { FontAwesome5,  Ionicons, MaterialIcons, Octicons, AntDesign } from '@expo/vector-icons'
import { useState } from 'react';
import GamesList from './src/views/list';

export default function App() {
  const [mostrar, setMostrar] = useState(false)
  return (
    <View style={styles.container}>
      <Text  style={styles.text}>Bienvenido.</Text>
      <StatusBar style="auto" />


      <View  style={styles.view1}>
        <TouchableOpacity onPress={() => setMostrar(!mostrar)}>
          <AntDesign name="down" color={"red"} size={30}/>
        </TouchableOpacity>

        {
          mostrar
            ? <GamesList/>
            : <></>
        }
        
      </View>

    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#ffff',
    alignItems:'center',
    justifyContent: 'center',
    backgroundColor: '#5c5b58'
  },

  view1:{
    position: 'relative',
    marginTop: 30,
  },
  text:{
    fontWeight: 'bold',
    top:-350,
    fontSize: 25,
    color: "#ffff",
  }
});
