import { StatusBar } from 'expo-status-bar';
import { StyleSheet, Text, Button, TouchableOpacity, View } from 'react-native';
import { FontAwesome5, Ionicons, MaterialIcons, Octicons, AntDesign } from '@expo/vector-icons'
import { useState } from 'react';
import GamesList from './GamesList';




export default function Home({ navigation }) {

  const [mostrar, setMostrar] = useState(false)




  return (
    <View style={styles.container}>

      <Button
        onPress={() => {

          navigation.navigate('gamesList')
        }}
        title="Mostrar"
        color="green"
      />
      <Text style={styles.text}>Bienvenido</Text>
      <Text style={styles.text2}>  Ã‰sta app muestra una lista de juegos consumida a traves de una API. </Text>
      <StatusBar style="auto" />


      <View style={styles.view1}>
        <TouchableOpacity onPress={() => setMostrar(!mostrar)}>

          <AntDesign name="pluscircle" color={"white"} size={30} />
        </TouchableOpacity>

        {
          mostrar
            ? <GamesList />
            : <></>
        }

      </View>

    </View>
  );
}
const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#ffff',
    alignItems: 'center',
    justifyContent: 'center',
    backgroundColor: '#5c5b58'
  },
  view1: {
    position: 'relative',
    marginTop: 30,
  },
  text: {
    fontWeight: 'bold',
    top: -300,
    fontSize: 25,
    color: "#ffff",
  },
  text2: {
    position: 'relative',
    margin: 10,
    borderWidth: 1,

  },
});
